import news from './mockNews'
import R from 'ramda'
export const fetchNewsApi = async () => {
    return new Promise(resolve => {
        resolve(news)
    })
}

export const fetchNewsByIdApi = async (id) => {
    return new Promise((resolve, reject) => {
        const newe = R.find(R.propEq('id', id), news)
        resolve(newe)
    })
}