import React ,{Component} from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import {fetchNewsById} from 'actions'
import {getNewsById} from 'selectors'
import R from 'ramda'
import { withRouter } from 'react-router-dom'
class News_page extends Component {
    componentDidMount() {
        window.scrollTo(0, 0)
    }
    componentDidMount() {
        this.props.fetchNewsById(this.props.params.id)
    }
    render() {
        const {newe} = this.props
        console.log(this.props.newe)
        return (
            <div>
                {newe.price}
            </div>
        )
    }
}


const mapStateToProps = state => {
    return {
        newe: getNewsById(state, state.newePage.id)
    }
}

const mapDispatchToProps = {
    fetchNewsById
}
export default withRouter( connect(mapStateToProps, mapDispatchToProps)(News_page))
